from numpy import sin, cos, zeros_like
def getF(g, l, m_block, k, m):
    def f(t, y):
        result = zeros_like(y)
        result[0,0] = y[0,1]
        result[0,1] = (-m[1]*(m[0] + m[1])*(g*sin(y[2,0]) - l[0]*y[1,1]**2*sin(y[1,0] - y[2,0]))*(-cos(y[1,0])*cos(y[1,0] - y[2,0]) + cos(y[2,0])) + (m[1]*cos(y[2,0])*cos(y[1,0] - y[2,0]) - (m[0] + m[1])*cos(y[1,0]))*(g*m[0]*sin(y[1,0]) + g*m[1]*sin(y[1,0]) + l[1]*m[1]*y[2,1]**2*sin(y[1,0] - y[2,0])) - (m[0] - m[1]*cos(y[1,0] - y[2,0])**2 + m[1])*(-k*y[0,0] + l[0]*m[0]*y[1,1]**2*sin(y[1,0]) + l[0]*m[1]*y[1,1]**2*sin(y[1,0]) + l[1]*m[1]*y[2,1]**2*sin(y[2,0])))/(-2.0*m[1]*(m[0] + m[1])*cos(y[1,0])*cos(y[2,0])*cos(y[1,0] - y[2,0]) + m[1]*(m[0] + m[1])*cos(y[2,0])**2 + m[1]*(m[0] + m[1] + m_block)*cos(y[1,0] - y[2,0])**2 + (m[0] + m[1])**2*cos(y[1,0])**2 - (m[0] + m[1])*(m[0] + m[1] + m_block))
        result[1,0] = y[1,1]
        result[1,1] = 0.5*(2.0*g*m[0]**2*sin(y[1,0]) + 2.0*g*m[0]*m[1]*sin(y[1,0]) + 2.0*g*m[0]*m_block*sin(y[1,0]) + g*m[1]*m_block*sin(y[1,0]) + g*m[1]*m_block*sin(y[1,0] - 2.0*y[2,0]) - 2.0*k*m[0]*y[0,0]*cos(y[1,0]) - k*m[1]*y[0,0]*cos(y[1,0]) + k*m[1]*y[0,0]*cos(y[1,0] - 2.0*y[2,0]) + l[0]*m[0]**2*y[1,1]**2*sin(2.0*y[1,0]) + l[0]*m[0]*m[1]*y[1,1]**2*sin(2.0*y[1,0]) + l[0]*m[1]*m_block*y[1,1]**2*sin(2.0*y[1,0] - 2.0*y[2,0]) + l[1]*m[0]*m[1]*y[2,1]**2*sin(y[1,0] - y[2,0]) + l[1]*m[0]*m[1]*y[2,1]**2*sin(y[1,0] + y[2,0]) + 2.0*l[1]*m[1]*m_block*y[2,1]**2*sin(y[1,0] - y[2,0]))/(l[0]*(-2.0*m[1]*(m[0] + m[1])*cos(y[1,0])*cos(y[2,0])*cos(y[1,0] - y[2,0]) + m[1]*(m[0] + m[1])*cos(y[2,0])**2 + m[1]*(m[0] + m[1] + m_block)*cos(y[1,0] - y[2,0])**2 + (m[0] + m[1])**2*cos(y[1,0])**2 - (m[0] + m[1])*(m[0] + m[1] + m_block)))
        result[2,0] = y[2,1]
        result[2,1] = (-(m[0] + m[1])*(g*sin(y[2,0]) - l[0]*y[1,1]**2*sin(y[1,0] - y[2,0]))*(-m[0] - m[1] - m_block + (m[0] + m[1])*cos(y[1,0])**2) + (m[0] + m[1])*(-cos(y[1,0])*cos(y[1,0] - y[2,0]) + cos(y[2,0]))*(-k*y[0,0] + l[0]*m[0]*y[1,1]**2*sin(y[1,0]) + l[0]*m[1]*y[1,1]**2*sin(y[1,0]) + l[1]*m[1]*y[2,1]**2*sin(y[2,0])) + ((m[0] + m[1])*cos(y[1,0])*cos(y[2,0]) - (m[0] + m[1] + m_block)*cos(y[1,0] - y[2,0]))*(g*m[0]*sin(y[1,0]) + g*m[1]*sin(y[1,0]) + l[1]*m[1]*y[2,1]**2*sin(y[1,0] - y[2,0])))/(l[1]*(-2.0*m[1]*(m[0] + m[1])*cos(y[1,0])*cos(y[2,0])*cos(y[1,0] - y[2,0]) + m[1]*(m[0] + m[1])*cos(y[2,0])**2 + m[1]*(m[0] + m[1] + m_block)*cos(y[1,0] - y[2,0])**2 + (m[0] + m[1])**2*cos(y[1,0])**2 - (m[0] + m[1])*(m[0] + m[1] + m_block)))
        return result
    return f
